<?php/* The GDPR Framework support functions------------------------------------------------------------------------------- */// Theme initif (!function_exists('ancora_gdpr_compliance_theme_setup')) {    add_action( 'ancora_action_before_init_theme', 'ancora_gdpr_compliance_theme_setup', 1 );    function ancora_gdpr_compliance_theme_setup() {        if (is_admin()) {            add_filter( 'ancora_filter_required_plugins', 'ancora_gdpr_compliance_required_plugins' );        }    }}// Check if Instagram Widget installed and activatedif ( !function_exists( 'ancora_exists_gdpr_compliance' ) ) {    function ancora_exists_gdpr_compliance() {        return defined( 'WP_GDPR_C_SLUG' );    }}// Filter to add in the required plugins listif ( !function_exists( 'ancora_gdpr_compliance_required_plugins' ) ) {        function ancora_gdpr_compliance_required_plugins($list=array()) {        if (in_array('gdpr-compliance', (array)ancora_get_global('required_plugins')))            $list[] = array(                'name'         => esc_html__('WP GDPR Compliance', 'blessing'),                'slug'         => 'wp-gdpr-compliance',                'required'     => false            );        return $list;    }}