<?php/* Gutenberg support functions------------------------------------------------------------------------------- */// Theme initif (!function_exists('ancora_gutenberg_theme_setup')) {    add_action( 'ancora_action_before_init_theme', 'ancora_gutenberg_theme_setup', 1 );    function ancora_gutenberg_theme_setup() {        if (is_admin()) {            add_filter( 'ancora_filter_required_plugins', 'ancora_gutenberg_required_plugins' );        }    }}// Check if Instagram Widget installed and activatedif ( !function_exists( 'ancora_exists_gutenberg' ) ) {    function ancora_exists_gutenberg() {        return function_exists( 'the_gutenberg_project' ) && function_exists( 'register_block_type' );    }}// Filter to add in the required plugins listif ( !function_exists( 'ancora_gutenberg_required_plugins' ) ) {        function ancora_gutenberg_required_plugins($list=array()) {        if (in_array('gutenberg', (array)ancora_get_global('required_plugins')))            $list[] = array(                'name'         => esc_html__('Gutenberg', 'blessing'),                'slug'         => 'gutenberg',                'required'     => false            );        return $list;    }}