<?php/** * ANCORA Framework: Testimonial post type settings * * @package	ancora * @since	ancora 1.0 */// Theme initif (!function_exists('ancora_testimonial_theme_setup')) {	add_action( 'ancora_action_before_init_theme', 'ancora_testimonial_theme_setup' );	function ancora_testimonial_theme_setup() {			// Add item in the admin menu        add_filter('trx_utils_filter_override_options',			'ancora_testimonial_add_override_options');		// Save data from meta box		add_action('save_post',				'ancora_testimonial_save_data');		// Meta box fields		global $ANCORA_GLOBALS;		$ANCORA_GLOBALS['testimonial_override_options'] = array(			'id' => 'testimonial-meta-box',			'title' => esc_html__('Testimonial Details', 'blessing'),			'page' => 'testimonial',			'context' => 'normal',			'priority' => 'high',			'fields' => array(				"testimonial_author" => array(					"title" => esc_html__('Testimonial author',  'blessing'),					"desc" => esc_html__("Name of the testimonial's author", 'blessing'),					"class" => "testimonial_author",					"std" => "",					"type" => "text"),				"testimonial_email" => array(					"title" => esc_html__("Author's e-mail",  'blessing'),					"desc" => esc_html__("E-mail of the testimonial's author - need to take Gravatar (if registered)", 'blessing'),					"class" => "testimonial_email",					"std" => "",					"type" => "text"),				"testimonial_link" => array(					"title" => esc_html__('Testimonial link',  'blessing'),					"desc" => esc_html__("URL of the testimonial source or author profile page", 'blessing'),					"class" => "testimonial_link",					"std" => "",					"type" => "text")			)		);				// Prepare type "Testimonial"        if(function_exists('ancora_require_data')){		ancora_require_data( 'post_type', 'testimonial', array(			'label'               => esc_html__( 'Testimonial', 'blessing' ),			'description'         => esc_html__( 'Testimonial Description', 'blessing' ),			'labels'              => array(				'name'                => esc_html_x ( 'Testimonials', 'Post Type General Name', 'blessing' ),				'singular_name'       => esc_html_x ( 'Testimonial', 'Post Type Singular Name', 'blessing' ),				'menu_name'           => esc_html__( 'Testimonials', 'blessing' ),				'parent_item_colon'   => esc_html__( 'Parent Item:', 'blessing' ),				'all_items'           => esc_html__( 'All Testimonials', 'blessing' ),				'view_item'           => esc_html__( 'View Item', 'blessing' ),				'add_new_item'        => esc_html__( 'Add New Testimonial', 'blessing' ),				'add_new'             => esc_html__( 'Add New', 'blessing' ),				'edit_item'           => esc_html__( 'Edit Item', 'blessing' ),				'update_item'         => esc_html__( 'Update Item', 'blessing' ),				'search_items'        => esc_html__( 'Search Item', 'blessing' ),				'not_found'           => esc_html__( 'Not found', 'blessing' ),				'not_found_in_trash'  => esc_html__( 'Not found in Trash', 'blessing' ),			),			'supports'            => array( 'title', 'editor', 'author', 'thumbnail'),			'hierarchical'        => false,			'public'              => false,			'show_ui'             => true,			'menu_icon'			  => 'dashicons-cloud',			'show_in_menu'        => true,			'show_in_nav_menus'   => true,			'show_in_admin_bar'   => true,			'menu_position'       => 25,			'can_export'          => true,			'has_archive'         => false,			'exclude_from_search' => true,			'publicly_queryable'  => false,			'capability_type'     => 'page',			)		);		// Prepare taxonomy for testimonial		ancora_require_data( 'taxonomy', 'testimonial_group', array(			'post_type'			=> array( 'testimonial' ),			'hierarchical'      => true,			'labels'            => array(				'name'              => esc_html_x ( 'Testimonials Group', 'taxonomy general name', 'blessing' ),				'singular_name'     => esc_html_x ( 'Group', 'taxonomy singular name', 'blessing' ),				'search_items'      => esc_html__( 'Search Groups', 'blessing' ),				'all_items'         => esc_html__( 'All Groups', 'blessing' ),				'parent_item'       => esc_html__( 'Parent Group', 'blessing' ),				'parent_item_colon' => esc_html__( 'Parent Group:', 'blessing' ),				'edit_item'         => esc_html__( 'Edit Group', 'blessing' ),				'update_item'       => esc_html__( 'Update Group', 'blessing' ),				'add_new_item'      => esc_html__( 'Add New Group', 'blessing' ),				'new_item_name'     => esc_html__( 'New Group Name', 'blessing' ),				'menu_name'         => esc_html__( 'Testimonial Group', 'blessing' ),			),			'show_ui'           => true,			'show_admin_column' => true,			'query_var'         => true,			'rewrite'           => array( 'slug' => 'testimonial_group' ),			)		);        }	}}// Add meta boxif (!function_exists('ancora_testimonial_add_override_options')) {		function ancora_testimonial_add_override_options($boxes = array()) {        $boxes[] = array_merge(ancora_get_global('testimonial_override_options'), array('callback' => 'ancora_testimonial_show_override_options'));        return $boxes;	}}// Callback function to show fields in meta boxif (!function_exists('ancora_testimonial_show_override_options')) {	function ancora_testimonial_show_override_options() {		global $post, $ANCORA_GLOBALS;		// Use nonce for verification		echo '<input type="hidden" name="override_options_testimonial_nonce" value="', wp_create_nonce(basename(__FILE__)), '" />';				$data = get_post_meta($post->ID, 'testimonial_data', true);			$fields = $ANCORA_GLOBALS['testimonial_override_options']['fields'];		?>		<table class="testimonial_area">		<?php		foreach ($fields as $id=>$field) { 			$meta = isset($data[$id]) ? $data[$id] : '';			?>			<tr class="testimonial_field <?php echo esc_attr($field['class']); ?>" valign="top">				<td><label for="<?php echo esc_attr($id); ?>"><?php echo esc_attr($field['title']); ?></label></td>				<td><input type="text" name="<?php echo esc_attr($id); ?>" id="<?php echo esc_attr($id); ?>" value="<?php echo esc_attr($meta); ?>" size="30" />					<br><small><?php echo esc_attr($field['desc']); ?></small></td>			</tr>			<?php		}		?>		</table>		<?php	}}// Save data from meta boxif (!function_exists('ancora_testimonial_save_data')) {		function ancora_testimonial_save_data($post_id) {		// verify nonce		if (!isset($_POST['override_options_testimonial_nonce']) || !wp_verify_nonce($_POST['override_options_testimonial_nonce'], basename(__FILE__))) {			return $post_id;		}		// check autosave		if (defined('DOING_AUTOSAVE') && DOING_AUTOSAVE) {			return $post_id;		}		// check permissions		if ($_POST['post_type']!='testimonial' || !current_user_can('edit_post', $post_id)) {			return $post_id;		}		global $ANCORA_GLOBALS;		$data = array();		$fields = $ANCORA_GLOBALS['testimonial_override_options']['fields'];		// Post type specific data handling		foreach ($fields as $id=>$field) { 			if (isset($_POST[$id])) 				$data[$id] = stripslashes($_POST[$id]);		}		update_post_meta($post_id, 'testimonial_data', $data);	}}?>